import '../App.css'

export const Message = ({author, text}) => {

  const message = () => {
    if (author==='robot') {
      <div className='robot'>
        <h2>Author: {author}</h2>
        <p>Text: {text}</p>
      </div>
    } else {
      <div className='author'>
      <h2>Author: {author}</h2>
      <p>Text: {text}</p>
    </div>
    }
  }
  return(
    <>
      <h2>Author: {author}</h2>
      <p>Text: {text}</p>
    </>
    )
}
